package com.project;

public class Conversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c ='V';
		int i = c+3;
		
		System.out.println("ASCII value of V increment to 3 is :"+i);
		System.out.println("Char value of V increment to 3 is :"+(char)i);

	}

}
